import React, { useState } from 'react';
import './createPost.css';

const CreatePost = (props) => {
  const [file, setFile]= useState("");
  let onPicChange=(e)=> {
    setFile(e.target.files[0]);
  };
  let onPicUpload = async (e)=> {
    e.preventDefault();
    const data = new FormData();
    data.append("file", file);
    fetch("/post/1",{
      method: "PUT",
      headers: {'Authorization': 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJkaGlyYWpAZ21haWwuY29tIiwiZXhwIjoxNzEzMjEzNDkzLCJpYXQiOjE3MTMxOTE4OTN9.c2ZcsQ-4EV3y88a1glk7L1yn9GxAAG_7DIOi-miyx6w'},
  body: data,
  }).then(response=> {
      if(response.ok){
          alert("Pic Updated Successfully");
      }
      else{
          alert("Pic Upload Failed");
      }
  }).catch(err=> {console.err("Error Uploading Files: ", err)})
  };

  return (
    <div className='createPost'>
        <div className='createPost-background'>
            <div className='createPost-top' onClick={props.toggle}>&times;</div>
            <div className='createPost-bottom'>
                <input type='file'></input>
                <button>Create Post</button>
            </div>
        </div>
    </div>
  )
}

export default CreatePost
